  setprop('/honeycomb/alpha/button-721/prop[0]/value/on',3);
  setprop('/honeycomb/alpha/button-722/prop[0]','/controls/engines/engine/magnetos-switch');
  setprop('/honeycomb/alpha/button-722/prop[0]/value/on',4);
  setprop('/honeycomb/alpha/button-722/prop[0]/value/off',3);
  setprop('/honeycomb/alpha/button-722/prop[1]','/controls/engines/engine/starter');
  setprop('/honeycomb/alpha/button-722/prop[1]/value/on',1);
  setprop('/honeycomb/alpha/button-722/prop[1]/value/off',0);
}else{
  setprop('/honeycomb/alpha/button-720/prop[0]','/controls/engines/engine/magnetos');
  setprop('/honeycomb/alpha/button-720/prop[0]/value/on','2');
  setprop('/honeycomb/alpha/button-721/prop[0]','/controls/engines/engine/magnetos');
  setprop('/honeycomb/alpha/button-721/prop[0]/value/on','3');
  setprop('/honeycomb/alpha/button-722/prop[0]','/controls/engines/engine/starter_cmd');
  setprop('/honeycomb/alpha/button-722/prop[0]/value/on',1);
  setprop('/honeycomb/alpha/button-722/prop[0]/value/off',0);
}
var getProps=func(prop){
  var idx = 0;
  var props =[];
  var propvalname = prop~'['~idx~']';
  var thisval = getprop(propvalname);
  var values=[];
  while (typeof(thisval)!='nil'){
    values = getPropValues(prop,idx);
    append (props,[thisval,values]);
    idx = idx +1;
    propvalname = prop~'['~idx~']';
    thisval = getprop(propvalname);
  }
  return props;
}

var getPropValues=func(prop,idx){
  var values =[];
  var sws = ['on','off'];
  foreach (var sw;sws){
    var propvalname = prop~'['~idx~']/value/'~sw;
    var thisval = getprop(propvalname);
    var valhash = [sw,thisval];
    append (values,valhash);
  }
  return values;
}

var setValue=func(prop,on){
  var props = getProps(prop);
  var sw = 'off';
  if (on){
    sw = 'on';
  }
  foreach (myprop;props){
    print ('PROPPPPP ',myprop[0]);
    foreach (myvalue;myprop[1]){
      if (sw==myvalue[0]){
        setprop(myprop[0],myvalue[1]);
      }
    }
  }
  return;
}
